
public class JavaBasics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// while loop ->loop the condition is checked prior to executing any statements
		int i = 10;
		while (i > 0) {
			System.out.println(i);
			i--;
		}
		// do while loop -> statements are run at least once, and then the condition is
		// verified.
		int j = 20;
		do {
			System.out.println(j);
			j++;
		} while (j < 30);// 1 loop of execution is guarantee
		
	}

}
